import React, { useState } from 'react';
import './booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'react-bootstrap';
import TextField from '@mui/material/TextField';
const Booking = ({ tour }) => {
  const { price, avgRating } = tour;

 

  const [credentials,setCredentials]= useState({
    userId: '01',
    userEmail: 'example@gamil.com',
    fullName:"",
    phone:"",
    guestSize:1,
    bookAt:''

})
const handleChange = e => {
    setCredentials(prev=>({...prev,[e.target.id]:e.t}))
}

 
 const totalAmount = Number(price) * Number(credentials.guestSize) + Number("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(credentials); 
  };

  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>â‚¹{price} <span>/per person</span></h3>
        <h4>{avgRating}<i style={{ color: 'orange' }} className="ri-star-fill"></i></h4>
      </div>
      <div className="booking__form">
        <h5>Information</h5>
        <Form className='booking__info-form' onSubmit={handleSubmit}>
          <FormGroup>
          <TextField id="fullName" label="Enter your name" variant="standard"  required/>
            {/* <input type="text" placeholder='Enter your name' id='fullName' required value=fullName} onChange={handleChange} /> */}
          </FormGroup>
          <FormGroup>
            {/* <input type="number" placeholder='Phone number' id='phone' required value={credentials.phone} onChange={handleChange} /> */}
            <TextField id="standard-basic" label="Phone number" variant="standard" />
          </FormGroup>
          <FormGroup className='d-flex align-items-center justify-content-between gap-3'>
            <input type="date" placeholder='' id='bookAt' required value={credentials.bookAt} onChange={""} />
            {/* <input type="number" placeholder='Guest' id='guestSize' required value={credentials.guestSize} onChange={handleChange} /> */}
            <TextField id="standard-basic" label="No.Of Guests" variant="standard" />
          </FormGroup>
          <Button type="submit" className='btn primary__btn w100 mt-4 form-control'>Book Now</Button>
        </Form>
      </div>
      <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className='border-0 px-0'>
            <h5 className='d-flex align-items-center gap-1'>
              â‚¹{price}<i className="ri-close-line"></i>1 person</h5>
            <span>â‚¹{price}</span>
          </ListGroupItem>
          <ListGroupItem className='border-0 px-0'>
            <h5>Service Charge + GST</h5>
            <span>â‚¹{}</span>
          </ListGroupItem>
          <ListGroupItem className='border-0 px-0 total'>
            <h5>Total</h5>
            <span>â‚¹{}</span>
          </ListGroupItem>
        </ListGroup>
       </div> 
    </div>
  );
};

export default Booking;
