import React, { useState } from 'react';
import './booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'react-bootstrap';

const Booking = ({ tour }) => {
  const { price, avgRating } = tour;

  const [credentials, setCredentials] = useState({
    fullName: '',
    phone: '',
    guestSize: 1,
    bookAt: ''
  });

  const handleChange = (e) => {
    const { id, value } = e.target;
    setCredentials((prev) => ({ ...prev, [id]: value }));
  };

  const serviceGst = 135;
//   const totalAmount = Number(price) * Number(credentials.guestSize) + Number(serviceGst);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(credentials); // You can add logic to submit data to a server here
  };

  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>₹{price} <span>/per person</span></h3>
        <h4>{avgRating}<i style={{ color: 'orange' }} className="ri-star-fill"></i></h4>
      </div>
      <div className="booking__form">
        <h5>Information</h5>
        <Form className='booking__info-form' onSubmit={handleSubmit}>
          <FormGroup>
            <input type="text" placeholder='Enter your name' id='fullName' required value={credentials.fullName} onChange={handleChange} />
          </FormGroup>
          <FormGroup>
            <input type="number" placeholder='Phone number' id='phone' required value={credentials.phone} onChange={handleChange} />
          </FormGroup>
          <FormGroup className='d-flex align-items-center justify-content-between gap-3'>
            <input type="date" placeholder='' id='bookAt' required value={credentials.bookAt} onChange={handleChange} />
            <input type="number" placeholder='Guest' id='guestSize' required value={credentials.guestSize} onChange={handleChange} />
          </FormGroup>
          <Button type="submit" className='btn primary__btn w100 mt-4 form-control'>Book Now</Button>
        </Form>
      </div>
      {/* <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className='border-0 px-0'>
            <h5 className='d-flex align-items-center gap-1'>
              ₹{price}<i className="ri-close-line"></i>1 person</h5>
            <span>₹{price}</span>
          </ListGroupItem>
          <ListGroupItem className='border-0 px-0'>
            <h5>Service Charge + GST</h5>
            <span>₹{serviceGst}</span>
          </ListGroupItem>
          <ListGroupItem className='border-0 px-0 total'>
            <h5>Total</h5>
            <span>₹{totalAmount}</span>
          </ListGroupItem>
        </ListGroup> */}
      {/* </div> */}
    </div>
  );
};

export default Booking;
