import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';
import { Container, Row, Button } from 'reactstrap';
import logo from '../../assets/images/IMG_20240317_201244.jpg';
import './header.css';

const nav__link = [
  {
    path: '/home',
    display: 'Home'
  },
  {
    path: '#',
    display: 'About'
  },
  {
    path: '/tours',
    display: 'Tours'
  },
];

const Header = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <header className={`header ${isDarkMode ? 'dark-mode' : ''}`}>
      <Container>
        <Row>
          <div className="nav__wrapper d-flex align-items-center justify-content-between">
            {/* Logo */}
            <div className="logo">
              <img src={logo} alt="" />
            </div>
            {/* menu */}
            <div className="navigation">
              <ul className="menu d-flex align-items-center gap-5">
                {nav__link.map((item, index) => (
                  <li className="nav__item" key={index}>
                    <NavLink
                      to={item.path}
                      activeClassName="active__link"
                      exact={item.path === '/home'}
                    >
                      {item.display}
                    </NavLink>
                  </li>
                ))}
              </ul>
            </div>
            {/* buttons */}
            <div className="nav__right d-flex align-items-center gap-4">
              <div className="nav__btns d-flex align-items-center gap-4">
                <Button className='btn'><Link to={'/login'} style={{color}}>
                  Login
                </Link></Button>
                <Button className='btn primary__btn'><Link to={'/register'}>
                  Register
                </Link></Button>
              </div>
              <span className='mobile__menu'>
                <Link>
                  <i className="ri-menu-line"></i>
                </Link>
              </span>
              <Button className="btn toggle-mode" onClick={toggleDarkMode}>
                {isDarkMode ? <i className="ri-sun-line"></i> : <i className="ri-moon-line"></i>}
              </Button>
            </div>
          </div>
        </Row>
      </Container>
    </header>
  );
};

export default Header;
